import React, { useState } from 'react';
import './App.css';

function App() {
  // Ê†áÁ≠æÈ°µÁä∂ÊÄÅ
  const [tabs, setTabs] = useState([
    { id: 1, title: 'ÊñáÊ°£1.md', content: '# Ê†áÈ¢ò1\n\nËøôÊòØÁ¨¨‰∏Ä‰∏™ÊñáÊ°£ÁöÑÂÜÖÂÆπ„ÄÇ\n\n## Â≠êÊ†áÈ¢ò\n\n- ÂàóË°®È°π1\n- ÂàóË°®È°π2\n- ÂàóË°®È°π3\n\n**Âä†Á≤óÊñáÊú¨**' },
    { id: 2, title: 'ÊñáÊ°£2.md', content: '# Ê†áÈ¢ò2\n\nËøôÊòØÁ¨¨‰∫å‰∏™ÊñáÊ°£ÁöÑÂÜÖÂÆπ„ÄÇ\n\n```javascript\nconsole.log("Hello World");\n```\n\n| Ë°®Ê†º | Á§∫‰æã |\n|------|------|\n| ÂçïÂÖÉÊ†º1 | ÂçïÂÖÉÊ†º2 |\n| ÂçïÂÖÉÊ†º3 | ÂçïÂÖÉÊ†º4 |' }
  ]);
  const [activeTabId, setActiveTabId] = useState(1);

  // ÁõÆÂΩïÁªìÊûÑÁä∂ÊÄÅ
  const [directoryStructure] = useState([
    {
      id: 'folder1',
      name: 'È°πÁõÆÊñáÊ°£',
      type: 'folder',
      expanded: true,
      children: [
        { id: 1, name: 'ÊñáÊ°£1.md', type: 'file' },
        { id: 2, name: 'ÊñáÊ°£2.md', type: 'file' },
        { id: 3, name: 'ÊñáÊ°£3.md', type: 'file' }
      ]
    },
    {
      id: 'folder2',
      name: 'ÂèÇËÄÉËµÑÊñô',
      type: 'folder',
      expanded: false,
      children: [
        { id: 4, name: 'ÂèÇËÄÉ1.md', type: 'file' },
        { id: 5, name: 'ÂèÇËÄÉ2.md', type: 'file' }
      ]
    },
    {
      id: 6,
      name: 'Áã¨Á´ãÊñáÊ°£.md',
      type: 'file'
    }
  ]);

  // ÂΩìÂâçÊ¥ªÂä®Ê†áÁ≠æÈ°µ
  const activeTab = tabs.find(tab => tab.id === activeTabId);

  // ÂàáÊç¢Ê†áÁ≠æÈ°µ
  const handleTabClick = (tabId) => {
    setActiveTabId(tabId);
  };

  // Ê∑ªÂä†Êñ∞Ê†áÁ≠æÈ°µ
  const handleAddTab = () => {
    const newTabId = tabs.length > 0 ? Math.max(...tabs.map(t => t.id)) + 1 : 1;
    const newTab = {
      id: newTabId,
      title: `Êñ∞ÊñáÊ°£${newTabId}.md`,
      content: '# Êñ∞ÊñáÊ°£\n\nÂú®ËøôÈáåËæìÂÖ•ÊÇ®ÁöÑÂÜÖÂÆπ...'
    };
    setTabs([...tabs, newTab]);
    setActiveTabId(newTabId);
  };

  // ÂÖ≥Èó≠Ê†áÁ≠æÈ°µ
  const handleCloseTab = (e, tabId) => {
    e.stopPropagation();
    if (tabs.length <= 1) return; // Ëá≥Â∞ë‰øùÁïô‰∏Ä‰∏™Ê†áÁ≠æÈ°µ

    const newTabs = tabs.filter(tab => tab.id !== tabId);
    setTabs(newTabs);

    // Â¶ÇÊûúÂÖ≥Èó≠ÁöÑÊòØÂΩìÂâçÊ¥ªÂä®Ê†áÁ≠æÈ°µÔºåÂàáÊç¢Âà∞Á¨¨‰∏Ä‰∏™Ê†áÁ≠æÈ°µ
    if (tabId === activeTabId) {
      setActiveTabId(newTabs[0].id);
    }
  };

  // Êõ¥Êñ∞ÊñáÊ°£ÂÜÖÂÆπ
  const handleContentChange = (e) => {
    const updatedTabs = tabs.map(tab => 
      tab.id === activeTabId 
        ? { ...tab, content: e.target.value } 
        : tab
    );
    setTabs(updatedTabs);
  };

  // ÂàáÊç¢Êñá‰ª∂Â§πÂ±ïÂºÄ/Êî∂Ëµ∑Áä∂ÊÄÅ
  const toggleFolder = (folderId) => {
    // Âú®Ëøô‰∏™ÁÆÄÂåñÁâàÊú¨‰∏≠ÔºåÊàë‰ª¨‰∏çÂÆûÈôÖÊõ¥Êñ∞Áä∂ÊÄÅÔºå‰ΩÜÂú®ÂÆåÊï¥ÂÆûÁé∞‰∏≠‰ºöËøôÊ†∑ÂÅö
    console.log(`ÂàáÊç¢Êñá‰ª∂Â§π ${folderId} ÁöÑÂ±ïÂºÄÁä∂ÊÄÅ`);
  };

  // Ê∏≤ÊüìÁõÆÂΩïÈ°π
  const renderDirectoryItem = (item, level = 0) => {
    const indent = { paddingLeft: `${level * 20 + 10}px` };

    if (item.type === 'folder') {
      return (
        <div key={item.id}>
          <div 
            className="directory-item folder"
            style={indent}
            onClick={() => toggleFolder(item.id)}
          >
            <span className="folder-icon">{item.expanded ? 'üìÇ' : 'üìÅ'}</span>
            <span className="folder-name">{item.name}</span>
          </div>
          {item.expanded && item.children && (
            <div className="folder-children">
              {item.children.map(child => renderDirectoryItem(child, level + 1))}
            </div>
          )}
        </div>
      );
    } else {
      return (
        <div 
          key={item.id}
          className={`directory-item file ${activeTabId === item.id ? 'active' : ''}`}
          style={indent}
          onClick={() => handleTabClick(item.id)}
        >
          <span className="file-icon">üìÑ</span>
          <span className="file-name">{item.name}</span>
        </div>
      );
    }
  };

  return (
    <div className="App">
      <div className="app-container">
        <div className="editor-container">
          <div className="tabs-container">
            <div className="tabs">
              {tabs.map(tab => (
                <div 
                  key={tab.id}
                  className={`tab ${tab.id === activeTabId ? 'active' : ''}`}
                  onClick={() => handleTabClick(tab.id)}
                >
                  <span className="tab-title">{tab.title}</span>
                  <span 
                    className="tab-close"
                    onClick={(e) => handleCloseTab(e, tab.id)}
                  >
                    √ó
                  </span>
                </div>
              ))}
              <button className="add-tab-button" onClick={handleAddTab}>+</button>
            </div>
          </div>
          <div className="editor-content">
            <textarea 
              className="document-editor markdown-editor"
              placeholder="Âú®Ê≠§Â§ÑËæìÂÖ•MarkdownÊ†ºÂºèÁöÑÊñáÊ°£ÂÜÖÂÆπ..."
              value={activeTab ? activeTab.content : ''}
              onChange={handleContentChange}
            />
          </div>
        </div>
        <div className="right-panel">
          <div className="directory-container">
            <div className="directory-header">
              <h2>ËµÑÊ∫êÁÆ°ÁêÜÂô®</h2>
              <div className="directory-actions">
                <button className="add-folder-btn">+</button>
              </div>
            </div>
            <div className="directory-content">
              {directoryStructure.map(item => renderDirectoryItem(item))}
            </div>
          </div>
          <div className="chat-container">
            <div className="chat-header">
              <h2>AI Âä©Êâã</h2>
            </div>
            <div className="chat-messages">
              <div className="message received">
                <div className="message-content">
                  ÊÇ®Â•ΩÔºÅÊàëÊòØÊÇ®ÁöÑAIÂä©Êâã„ÄÇÊÇ®ÂèØ‰ª•Âú®Â∑¶‰æßÁºñËæëMarkdownÊñáÊ°£ÔºåÁÑ∂ÂêéÂú®ËøôÈáå‰∏éÊàëËÆ®ËÆ∫ÊÇ®ÁöÑÂÜÖÂÆπ„ÄÇ
                </div>
                <div className="message-time">Áé∞Âú®</div>
              </div>
            </div>
            <div className="chat-input-container">
              <textarea 
                className="chat-input"
                placeholder="ËæìÂÖ•Ê∂àÊÅØ..."
              />
              <button className="send-button">ÂèëÈÄÅ</button>
            </div>
          </div>
