import React, { useState, useRef, useEffect } from 'react';
import './App.css';
import axios from 'axios';

// ËÆæÁΩÆAPIÂü∫Á°ÄURL
const API_BASE_URL = 'http://localhost:8000/api/documents';

function App() {
  // Ê†áÁ≠æÈ°µÁä∂ÊÄÅ
  const [tabs, setTabs] = useState([]);
  const [activeTabId, setActiveTabId] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  // ÁõÆÂΩïÁªìÊûÑÁä∂ÊÄÅ
  const [directoryStructure, setDirectoryStructure] = useState([
    {
      id: 'folder1',
      name: 'Á¨îËÆ∞‰ø°ÊÅØ',
      type: 'folder',
      expanded: true,
      children: []
    }
  ]);

  // ÂºïÁî®ÂÖÉÁ¥†
  const tabsContainerRef = useRef(null);
  const directoryContentRef = useRef(null);
  const activeTabRef = useRef(null);
  const activeDirectoryItemRef = useRef(null);

  // ÂΩìÂâçÊ¥ªÂä®Ê†áÁ≠æÈ°µ
  const activeTab = tabs.find(tab => tab.id === activeTabId);

  // ÁªÑ‰ª∂Âä†ËΩΩÊó∂‰ªéÂêéÁ´ØËé∑ÂèñÊñáÊ°£Êï∞ÊçÆ
  useEffect(() => {
    fetchDocuments();
  }, []);

  // ‰ªéÂêéÁ´ØËé∑ÂèñÊâÄÊúâÊñáÊ°£
  const fetchDocuments = async () => {
    try {
      setIsLoading(true);
      const response = await axios.get(API_BASE_URL);
      const documents = response.data;

      if (documents.length > 0) {
        // Â∞ÜÊñáÊ°£ËΩ¨Êç¢‰∏∫Ê†áÁ≠æÈ°µÊ†ºÂºè
        const newTabs = documents.map(doc => ({
          id: doc.id,
          title: doc.title,
          content: doc.content
        }));

        setTabs(newTabs);
        setActiveTabId(documents[0].id);

        // ÊûÑÂª∫ÁõÆÂΩïÁªìÊûÑ
        const fileItems = documents.map(doc => ({
          id: doc.id,
          name: doc.title,
          type: 'file'
        }));

        setDirectoryStructure([
          {
            id: 'folder1',
            name: 'Á¨îËÆ∞‰ø°ÊÅØ',
            type: 'folder',
            expanded: true,
            children: fileItems
          }
        ]);
      } else {
        // Â¶ÇÊûúÊ≤°ÊúâÊñáÊ°£ÔºåÂàõÂª∫‰∏Ä‰∏™ÈªòËÆ§ÊñáÊ°£
        createDefaultDocument();
      }
    } catch (error) {
      console.error('Ëé∑ÂèñÊñáÊ°£Â§±Ë¥•:', error);
      // Â¶ÇÊûúËé∑ÂèñÂ§±Ë¥•ÔºåÂàõÂª∫‰∏Ä‰∏™ÈªòËÆ§ÊñáÊ°£
      createDefaultDocument();
    } finally {
      setIsLoading(false);
    }
  };

  // ÂàõÂª∫ÈªòËÆ§ÊñáÊ°£
  const createDefaultDocument = async () => {
    try {
      const newDocument = {
        title: 'Êñ∞Âª∫ÊñáÊ°£1.md',
        content: '# Êñ∞Âª∫ÊñáÊ°£\n\nÂú®ËøôÈáåËæìÂÖ•ÊÇ®ÁöÑÂÜÖÂÆπ...'
      };

      const response = await axios.post(API_BASE_URL, newDocument);
      const createdDocument = response.data;

      const newTab = {
        id: createdDocument.id,
        title: createdDocument.title,
        content: createdDocument.content
      };

      setTabs([newTab]);
      setActiveTabId(createdDocument.id);

      // Êõ¥Êñ∞ÁõÆÂΩïÁªìÊûÑ
      setDirectoryStructure([
        {
          id: 'folder1',
          name: 'Á¨îËÆ∞‰ø°ÊÅØ',
          type: 'folder',
          expanded: true,
          children: [
            {
              id: createdDocument.id,
              name: createdDocument.title,
              type: 'file'
            }
          ]
        }
      ]);
    } catch (error) {
      console.error('ÂàõÂª∫ÈªòËÆ§ÊñáÊ°£Â§±Ë¥•:', error);
      // Â¶ÇÊûúÂêéÁ´ØÂàõÂª∫Â§±Ë¥•Ôºå‰ΩøÁî®Êú¨Âú∞ÈªòËÆ§ÊñáÊ°£
      const defaultTab = {
        id: 1,
        title: 'Êñ∞Âª∫ÊñáÊ°£1.md',
        content: '# Êñ∞Âª∫ÊñáÊ°£\n\nÂú®ËøôÈáåËæìÂÖ•ÊÇ®ÁöÑÂÜÖÂÆπ...'
      };

      setTabs([defaultTab]);
      setActiveTabId(1);
    }
  };

  // ‰øùÂ≠òÊñáÊ°£Âà∞ÂêéÁ´Ø
  const saveDocument = async (docId, title, content) => {
    try {
      await axios.put(`${API_BASE_URL}/${docId}`, {
        title: title,
        content: content
      });
      console.log(`ÊñáÊ°£ ${docId} ‰øùÂ≠òÊàêÂäü`);
    } catch (error) {
      console.error(`‰øùÂ≠òÊñáÊ°£ ${docId} Â§±Ë¥•:`, error);
    }
  };

  // ÂàáÊç¢Ê†áÁ≠æÈ°µ
  const handleTabClick = (tabId) => {
    setActiveTabId(tabId);
  };

  // Ê∑ªÂä†Êñ∞Ê†áÁ≠æÈ°µ
  const handleAddTab = async () => {
    try {
      // ÂàõÂª∫Êñ∞ÊñáÊ°£
      const maxId = Math.max(...tabs.map(t => typeof t.id === 'number' ? t.id : 0), 0);
      const newTitle = `Êñ∞Âª∫ÊñáÊ°£${maxId + 1}.md`;

      const newDocument = {
        title: newTitle,
        content: '# Êñ∞Âª∫ÊñáÊ°£\n\nÂú®ËøôÈáåËæìÂÖ•ÊÇ®ÁöÑÂÜÖÂÆπ...'
      };

      const response = await axios.post(API_BASE_URL, newDocument);
      const createdDocument = response.data;

      const newTab = {
        id: createdDocument.id,
        title: createdDocument.title,
        content: createdDocument.content
      };

      setTabs([...tabs, newTab]);
      setActiveTabId(createdDocument.id);

      // Êõ¥Êñ∞ÁõÆÂΩïÁªìÊûÑ
      const newFile = {
        id: createdDocument.id,
        name: createdDocument.title,
        type: 'file'
      };

      setDirectoryStructure(prev => {
        const newStructure = [...prev];
        const folder = newStructure.find(item => item.id === 'folder1');
        if (folder) {
          folder.children = [...folder.children, newFile];
        }
        return newStructure;
      });
    } catch (error) {
      console.error('ÂàõÂª∫Êñ∞ÊñáÊ°£Â§±Ë¥•:', error);
    }
  };

  // ÂÖ≥Èó≠Ê†áÁ≠æÈ°µ
  const handleCloseTab = async (e, tabId) => {
    e.stopPropagation();
    if (tabs.length <= 1) return; // Ëá≥Â∞ë‰øùÁïô‰∏Ä‰∏™Ê†áÁ≠æÈ°µ

    try {
      // ‰ªéÂêéÁ´ØÂà†Èô§ÊñáÊ°£
      await axios.delete(`${API_BASE_URL}/${tabId}`);

      const newTabs = tabs.filter(tab => tab.id !== tabId);
      setTabs(newTabs);

      // Â¶ÇÊûúÂÖ≥Èó≠ÁöÑÊòØÂΩìÂâçÊ¥ªÂä®Ê†áÁ≠æÈ°µÔºåÂàáÊç¢Âà∞Á¨¨‰∏Ä‰∏™Ê†áÁ≠æÈ°µ
      if (tabId === activeTabId) {
        setActiveTabId(newTabs[0].id);
      }

      // Êõ¥Êñ∞ÁõÆÂΩïÁªìÊûÑ
      setDirectoryStructure(prev => {
        const newStructure = [...prev];
        const folder = newStructure.find(item => item.id === 'folder1');
        if (folder) {
          folder.children = folder.children.filter(child => child.id !== tabId);
        }
        return newStructure;
      });
    } catch (error) {
      console.error(`Âà†Èô§ÊñáÊ°£ ${tabId} Â§±Ë¥•:`, error);
    }
  };

  // Êõ¥Êñ∞ÊñáÊ°£ÂÜÖÂÆπ
  const handleContentChange = (e) => {
    const updatedTabs = tabs.map(tab => 
      tab.id === activeTabId 
        ? { ...tab, content: e.target.value } 
        : tab
    );
    setTabs(updatedTabs);
  };

  // ‰øùÂ≠òÊñáÊ°£ÔºàCmd+SÔºâ
  const handleSaveDocument = async () => {
    if (activeTab) {
      await saveDocument(activeTab.id, activeTab.title, activeTab.content);
    }
  };

  // Â§ÑÁêÜÈîÆÁõò‰∫ã‰ª∂
  useEffect(() => {
    const handleKeyDown = (e) => {
      if ((e.ctrlKey || e.metaKey) && e.key === 's') {
        e.preventDefault();
        handleSaveDocument();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [activeTab]);

  // ÂàáÊç¢Êñá‰ª∂Â§πÂ±ïÂºÄ/Êî∂Ëµ∑Áä∂ÊÄÅ
  const toggleFolder = (folderId) => {
    const updateFolderExpanded = (items) => {
      return items.map(item => {
        if (item.type === 'folder' && item.id === folderId) {
          return { ...item, expanded: !item.expanded };
        } else if (item.type === 'folder' && item.children) {
          return { ...item, children: updateFolderExpanded(item.children) };
        }
        return item;
      });
    };

    setDirectoryStructure(updateFolderExpanded(directoryStructure));
  };

  // Ê∑ªÂä†Êñ∞Êñá‰ª∂Â§π
  const addNewFolder = () => {
    const newFolder = {
      id: `folder${Date.now()}`,
      name: 'Êñ∞Âª∫Êñá‰ª∂Â§π',
      type: 'folder',
      expanded: true,
      children: []
    };

    setDirectoryStructure([newFolder, ...directoryStructure]);
  };

  // Ê∑ªÂä†Êñ∞Êñá‰ª∂
  const addNewFile = async () => {
    try {
      // ÊâæÂà∞ÊúÄÂ§ßÁöÑÊï∞Â≠óID
      const maxId = Math.max(...tabs.map(t => typeof t.id === 'number' ? t.id : 0), 0);
      const newTitle = `Êñ∞Âª∫ÊñáÊ°£${maxId + 1}.md`;

      const newDocument = {
        title: newTitle,
        content: '# Êñ∞Âª∫ÊñáÊ°£\n\nÂú®ËøôÈáåËæìÂÖ•ÊÇ®ÁöÑÂÜÖÂÆπ...'
      };

      const response = await axios.post(API_BASE_URL, newDocument);
      const createdDocument = response.data;

      const newFile = {
        id: createdDocument.id,
        name: createdDocument.title,
        type: 'file'
      };

      // ÂàõÂª∫ÂØπÂ∫îÁöÑÊñ∞Ê†áÁ≠æÈ°µ
      const newTab = {
        id: createdDocument.id,
        title: createdDocument.title,
        content: createdDocument.content
      };

      setTabs([...tabs, newTab]);
      setActiveTabId(createdDocument.id); // ÈÄâ‰∏≠Êñ∞Âª∫ÁöÑÊñáÊ°£

      // Â∞ÜÊñ∞Êñá‰ª∂Ê∑ªÂä†Âà∞Á¨îËÆ∞‰ø°ÊÅØÊñá‰ª∂Â§π
      setDirectoryStructure(prev => {
        const newStructure = [...prev];
        const folder = newStructure.find(item => item.id === 'folder1');
        if (folder) {
          folder.children = [...folder.children, newFile];
        }
        return newStructure;
      });
    } catch (error) {
      console.error('ÂàõÂª∫Êñ∞Êñá‰ª∂Â§±Ë¥•:', error);
    }
  };

  // ‰ªéÁõÆÂΩïÂå∫ÈÄâÊã©Êñá‰ª∂
  const handleFileSelect = (fileId) => {
    // Ê£ÄÊü•ËØ•Êñá‰ª∂ÊòØÂê¶Â∑≤ÁªèÂ≠òÂú®‰∫éÊ†áÁ≠æÈ°µ‰∏≠
    const existingTab = tabs.find(tab => tab.id === fileId);

    if (existingTab) {
      // Â¶ÇÊûúÊñá‰ª∂Â∑≤Â≠òÂú®ÔºåÁõ¥Êé•ÈÄâ‰∏≠
      setActiveTabId(fileId);
    } else {
      // Â¶ÇÊûúÊñá‰ª∂‰∏çÂ≠òÂú®‰∫éÊ†áÁ≠æÈ°µ‰∏≠ÔºåÂú®Ê†áÁ≠æÈ°µÊú´Â∞æËøΩÂä†Êñ∞Ê†áÁ≠æ
      // È¶ñÂÖàÂú®ÁõÆÂΩïÁªìÊûÑ‰∏≠ÊâæÂà∞ËØ•Êñá‰ª∂
      const findFileInDirectory = (items) => {
        for (const item of items) {
          if (item.type === 'file' && item.id === fileId) {
            return item;
          } else if (item.type === 'folder' && item.children) {
            const found = findFileInDirectory(item.children);
            if (found) return found;
          }
        }
        return null;
      };

      const file = findFileInDirectory(directoryStructure);
      if (file) {
        // ‰ªéÂêéÁ´ØËé∑ÂèñÊñáÊ°£ÂÜÖÂÆπ
        axios.get(`${API_BASE_URL}/${fileId}`)
          .then(response => {
            const document = response.data;
            // ÂàõÂª∫Êñ∞ÁöÑÊ†áÁ≠æÈ°µÂπ∂ËøΩÂä†Âà∞Êú´Â∞æ
            const newTab = {
              id: document.id,
              title: document.title,
              content: document.content
            };

            setTabs([...tabs, newTab]);
            setActiveTabId(document.id);
          })
          .catch(error => {
            console.error('Ëé∑ÂèñÊñáÊ°£ÂÜÖÂÆπÂ§±Ë¥•:', error);
            // Â¶ÇÊûúËé∑ÂèñÂ§±Ë¥•Ôºå‰ΩøÁî®ÈªòËÆ§ÂÜÖÂÆπ
            const newTab = {
              id: fileId,
              title: file.name,
              content: '# ' + file.name.replace('.md', '') + '\n\nÂú®ËøôÈáåËæìÂÖ•ÊÇ®ÁöÑÂÜÖÂÆπ...'
            };

            setTabs([...tabs, newTab]);
            setActiveTabId(fileId);
          });
      }
    }
  };

  // ÂΩìÊ¥ªÂä®Ê†áÁ≠æÈ°µÊîπÂèòÊó∂ÔºåÊªöÂä®Âà∞ÂèØËßÅ‰ΩçÁΩÆ
  useEffect(() => {
    // ÊªöÂä®Ê†áÁ≠æÈ°µÂà∞ÂèØËßÅ‰ΩçÁΩÆ
    if (tabsContainerRef.current) {
      // ÊâæÂà∞Ê¥ªÂä®Ê†áÁ≠æÈ°µÁöÑDOMÂÖÉÁ¥†
      const activeTabElement = document.querySelector('.tab.active');
      if (activeTabElement) {
        const container = tabsContainerRef.current;
        const containerRect = container.getBoundingClientRect();
        const activeTabRect = activeTabElement.getBoundingClientRect();

        // Â¶ÇÊûúÊ¥ªÂä®Ê†áÁ≠æÈ°µ‰∏çÂú®ÂèØËßÅÂå∫ÂüüÂÜÖÔºåÂàôÊªöÂä®Âà∞ÂèØËßÅ‰ΩçÁΩÆ
        if (activeTabRect.left < containerRect.left) {
          container.scrollLeft += activeTabRect.left - containerRect.left - 10;
        } else if (activeTabRect.right > containerRect.right) {
          container.scrollLeft += activeTabRect.right - containerRect.right + 10;
        }
      }
    }

    // ÊªöÂä®ÁõÆÂΩïÈ°πÂà∞ÂèØËßÅ‰ΩçÁΩÆ
    if (activeDirectoryItemRef.current && directoryContentRef.current) {
      const container = directoryContentRef.current;
      const activeItem = activeDirectoryItemRef.current;

      const containerRect = container.getBoundingClientRect();
      const activeItemRect = activeItem.getBoundingClientRect();

      // Â¶ÇÊûúÊ¥ªÂä®ÁõÆÂΩïÈ°π‰∏çÂú®ÂèØËßÅÂå∫ÂüüÂÜÖÔºåÂàôÊªöÂä®Âà∞ÂèØËßÅ‰ΩçÁΩÆ
      if (activeItemRect.top < containerRect.top) {
        container.scrollTop += activeItemRect.top - containerRect.top - 10;
      } else if (activeItemRect.bottom > containerRect.bottom) {
        container.scrollTop += activeItemRect.bottom - containerRect.bottom + 10;
      }
    }
  }, [activeTabId]);

  // Ê∏≤ÊüìÁõÆÂΩïÈ°π
  const renderDirectoryItem = (item, level = 0) => {
    const indent = { paddingLeft: `${level * 20 + 10}px` };

    if (item.type === 'folder') {
      return (
        <div key={item.id}>
          <div 
            className="directory-item folder"
            style={indent}
          >
            <span 
              className="folder-toggle"
              onClick={() => toggleFolder(item.id)}
            >
              {item.expanded ? '‚ñº' : '‚ñ∂'}
            </span>
            <span className="folder-icon">üìÅ</span>
            <span className="folder-name">{item.name}</span>
          </div>
          {item.expanded && item.children && (
            <div className="folder-children">
              {item.children.map(child => renderDirectoryItem(child, level + 1))}
            </div>
          )}
        </div>
      );
    } else {
      return (
        <div 
          ref={activeTabId === item.id ? activeDirectoryItemRef : null}
          key={item.id}
          className={`directory-item file ${activeTabId === item.id ? 'active' : ''}`}
          style={indent}
          onClick={() => handleFileSelect(item.id)}
        >
          <span className="file-icon">üìÑ</span>
          <span className="file-name">{item.name}</span>
        </div>
