import React, { useState } from 'react';
import './App.css';

function App() {
  // 标签页状态
  const [tabs, setTabs] = useState([
    { id: 1, title: '文档1.md', content: '# 标题1\n\n这是第一个文档的内容。\n\n## 子标题\n\n- 列表项1\n- 列表项2\n- 列表项3\n\n**加粗文本**' },
    { id: 2, title: '文档2.md', content: '# 标题2\n\n这是第二个文档的内容。\n\n```javascript\nconsole.log("Hello World");\n```\n\n| 表格 | 示例 |\n|------|------|\n| 单元格1 | 单元格2 |\n| 单元格3 | 单元格4 |' }
  ]);
  const [activeTabId, setActiveTabId] = useState(1);

  // 目录结构状态
  const [directoryStructure, setDirectoryStructure] = useState([
    {
      id: 'folder1',
      name: '项目文档',
      type: 'folder',
      expanded: true,
      children: [
        { id: 1, name: '文档1.md', type: 'file' },
        { id: 2, name: '文档2.md', type: 'file' },
        { id: 3, name: '文档3.md', type: 'file' }
      ]
    },
    {
      id: 'folder2',
      name: '参考资料',
      type: 'folder',
      expanded: false,
      children: [
        { id: 4, name: '参考1.md', type: 'file' },
        { id: 5, name: '参考2.md', type: 'file' }
      ]
    },
    {
      id: 6,
      name: '独立文档.md',
      type: 'file'
    }
  ]);

  // 当前活动标签页
  const activeTab = tabs.find(tab => tab.id === activeTabId);

  // 切换标签页
  const handleTabClick = (tabId) => {
    setActiveTabId(tabId);
  };

  // 添加新标签页
  const handleAddTab = () => {
    const newTabId = tabs.length > 0 ? Math.max(...tabs.map(t => t.id)) + 1 : 1;
    const newTab = {
      id: newTabId,
      title: `新文档${newTabId}.md`,
      content: '# 新文档\n\n在这里输入您的内容...'
    };
    setTabs([...tabs, newTab]);
    setActiveTabId(newTabId);

    // 将新文档添加到目录区
    const newFile = {
      id: newTabId,
      name: `新文档${newTabId}.md`,
      type: 'file'
    };

    setDirectoryStructure([
      ...directoryStructure,
      newFile
    ]);
  };

  // 关闭标签页
  const handleCloseTab = (e, tabId) => {
    e.stopPropagation();
    if (tabs.length <= 1) return; // 至少保留一个标签页

    const newTabs = tabs.filter(tab => tab.id !== tabId);
    setTabs(newTabs);

    // 如果关闭的是当前活动标签页，切换到第一个标签页
    if (tabId === activeTabId) {
      setActiveTabId(newTabs[0].id);
    }
  };

  // 更新文档内容
  const handleContentChange = (e) => {
    const updatedTabs = tabs.map(tab => 
      tab.id === activeTabId 
        ? { ...tab, content: e.target.value } 
        : tab
    );
    setTabs(updatedTabs);
  };

  // 切换文件夹展开/收起状态
  const toggleFolder = (folderId) => {
    const updateFolderExpanded = (items) => {
      return items.map(item => {
        if (item.type === 'folder' && item.id === folderId) {
          return { ...item, expanded: !item.expanded };
        } else if (item.type === 'folder' && item.children) {
          return { ...item, children: updateFolderExpanded(item.children) };
        }
        return item;
      });
    };

    setDirectoryStructure(updateFolderExpanded(directoryStructure));
  };

  // 添加新文件夹
  const addNewFolder = () => {
    const newFolder = {
      id: `folder${Date.now()}`,
      name: '新建文件夹',
      type: 'folder',
      expanded: true,
      children: []
    };

    setDirectoryStructure([newFolder, ...directoryStructure]);
  };

  // 添加新文件
  const addNewFile = () => {
    const newFileId = Date.now();
    const newFile = {
      id: newFileId,
      name: `新建文档${newFileId}.md`,
      type: 'file'
    };

    // 创建对应的新标签页
    const newTab = {
      id: newFileId,
      title: `新建文档${newFileId}.md`,
      content: '# 新文档\n\n在这里输入您的内容...'
    };

    setTabs([...tabs, newTab]);
    setActiveTabId(newFileId); // 选中新建的文档

    // 将新文件添加到根目录
    setDirectoryStructure([
      ...directoryStructure,
      newFile
    ]);
  };

  // 从目录区选择文件
  const handleFileSelect = (fileId) => {
    setActiveTabId(fileId);
  };

  // 渲染目录项
  const renderDirectoryItem = (item, level = 0) => {
    const indent = { paddingLeft: `${level * 20 + 10}px` };

    if (item.type === 'folder') {
      return (
        <div key={item.id}>
          <div 
            className="directory-item folder"
            style={indent}
          >
            <span 
              className="folder-toggle"
              onClick={() => toggleFolder(item.id)}
            >
              {item.expanded ? '▼' : '▶'}
            </span>
            <span className="folder-icon">📁</span>
            <span className="folder-name">{item.name}</span>
          </div>
          {item.expanded && item.children && (
            <div className="folder-children">
              {item.children.map(child => renderDirectoryItem(child, level + 1))}
            </div>
          )}
        </div>
      );
    } else {
      return (
        <div 
          key={item.id}
          className={`directory-item file ${activeTabId === item.id ? 'active' : ''}`}
          style={indent}
          onClick={() => handleFileSelect(item.id)}
        >
          <span className="file-icon">📄</span>
          <span className="file-name">{item.name}</span>
        </div>
      );
    }
  };

  return (
    <div className="App">
      <div className="app-container">
        <div className="editor-container">
          <div className="tabs-container">
            <div className="tabs">
              {tabs.map(tab => (
                <div 
                  key={tab.id}
                  className={`tab ${tab.id === activeTabId ? 'active' : ''}`}
                  onClick={() => handleTabClick(tab.id)}
                >
                  <span className="tab-title">{tab.title}</span>
                  <span 
                    className="tab-close"
                    onClick={(e) => handleCloseTab(e, tab.id)}
                  >
                    ×
                  </span>
                </div>
              ))}
              <button className="add-tab-button" onClick={handleAddTab}>+</button>
            </div>
          </div>
          <div className="editor-content">
            <textarea 
              className="document-editor markdown-editor"
              placeholder="在此处输入Markdown格式的文档内容..."
              value={activeTab ? activeTab.content : ''}
              onChange={handleContentChange}
            />
          </div>
        </div>
        <div className="right-panel">
          <div className="directory-container">
            <div className="directory-header">
              <h2>资源管理器</h2>
              <div className="directory-actions">
                <button className="add-folder-btn" onClick={addNewFolder}>📁</button>
                <button className="add-file-btn" onClick={addNewFile}>📄</button>
              </div>
            </div>
            <div className="directory-content">
              {directoryStructure.map(item => renderDirectoryItem(item))}
            </div>
          </div>
          <div className="chat-container">
            <div className="chat-header">
              <h2>AI 助手</h2>
            </div>
            <div className="chat-messages">
              <div className="message received">
                <div className="message-content">
                  您好！我是您的AI助手。您可以在左侧编辑Markdown文档，然后在这里与我讨论您的内容。
                </div>
                <div className="message-time">现在</div>
              </div>
            </div>
            <div className="chat-input-container">
              <textarea 
                className="chat-input"
                placeholder="输入消息..."
              />
              <button className="send-button">发送</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
